
package learnjava7;

public class lec1arithmatic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//add 2 value respectivly 5 and 10 and print the value
		
		int a = 5;
		int b = 10;
		int sum;
		sum = a+b; 
		
		System.out.println("total number is" +sum);
		
		System.out.println(a);
	
		//subtracts 1 value from another
		int num1 =30;
		int num2 =15;
		int sub;
		
		sub = num2-num1;
		
		System.out.println("the sub is"+sub);
		
		// multiplies 2 value
		
		int m = 10;
		int n = 5;
		int mul;
		mul = m*n;
		System.out.println("the mul is"+ mul);
		
		
		//divides 1 value by another
		
		int k = 30;
		int v = 6;
		int div;
		div = k/v;
		System.out.println("the div is " + div);
		
		
		// return the division remainder
		int x = 40;
		int y = 30;
		int modul;
		modul = x%y;
		System.out.println("the modul is"+ modul);
		
		
		
	 
		
		
		
		
		
		
	}

}
